import React, { Component } from "react"
import "./style.css"
import Actions from "../../../";

class login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: "",
            local_pw: "",
            new:false
        }
    }

    handleSubmitAuth = (e) => {
        if(this.refs.submitForm.reportValidity()) {
            e.preventDefault();
            this.setState({
                loading: true
            })
        console.log("chubby bunny")


        const userData = {
            email: this.state.email,
            local_pw: this.state.local_pw
        }

        this.handleSignUp(userData);

    }
}

    handleSignUp = (userData) => {
        this.setState({
            new:true
        })
        Actions.handleSignUp(userData)
        .then(data=>{return data.json()})
        .catch(err=> console.log("err",err))
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;

        this.setState({
            [name]: value
        });
    }

    render() {
        return (
            <div id="formoutline">
                <h5>Employee Login</h5>
                <form ref="submitForm" >
                    <input
                        className="login"
                        id="emailInput"
                        placeholder="Email"
                        required={true}
                        // size="3"
                        name="email"
                        onChange={this.handleInputChange}>
                    </input>
                    <input
                        className="login"
                        id="passwordInput"
                        placeholder="Password"
                        required={true}
                        // size="6"
                        name="local_pw"
                        onChange={this.handleInputChange}>
                    </input>
                </form>
                <button id="submit" onClick={(e) => this.handleSubmitAuth(e)} >Submit</button>
            </div>
        );
    }
}

export default login;
